JpeV = 1.60218e-19; % J/eV

E_c = 0.66*JpeV; % J
E_v = 0*JpeV; % J
E_g = E_c-E_v;

m_o = 9.10938356e-31; % kg
m_h = 0.34*m_o; % kg
m_e = 0.22*m_o; % kg
k_B = 8.617333262145e-5*JpeV; % J/K
hbar = 4.135667696e-15/2/pi*JpeV; % J*s

N_V = @(Te) 2.*(m_h.*k_B.*Te./2./pi./hbar.^2).^(3/2);
N_C = @(Te) 2.*(m_e.*k_B.*Te./2./pi./hbar.^2).^(3/2);
chem_pot = @(Te) (E_c + E_v)/2 + k_B.*Te./2.*log(N_V(Te)./N_C(Te)); % J
fermi_dist = @(E,Te) (exp((E-chem_pot(Te))./k_B./Te) + 1).^(-1); % unitless pop/pop
dens_states = @(E,Te) (E > E_c).*m_e./(hbar.^2)./(pi.^2).*sqrt((2.*m_e.*(E-E_c))./hbar.^2) + N_C(Te); % 1/J/m^3 |||| 

tau = @(Te) integral(@(E)fermi_dist(E,Te).*dens_states(E,Te).*scattering_rate_con(E/JpeV,Te),0.66*JpeV,5*JpeV, 'ArrayValued', true)/integral(@(E)fermi_dist(E,Te).*dens_states(E,Te),0.66*JpeV,50*JpeV); % J/K/m^3

energyRange = 0.66:0.01:5; % eV
scattering_rate_con_res = zeros(1,length(energyRange));

for i = 1:length(energyRange)
    E = energyRange(i);
    scattering_rate_con_res(i) = fermi_dist(E*JpeV,1100).*dens_states(E*JpeV,1100).*scattering_rate_con(E,1100);
end

figure;
plot(energyRange,scattering_rate_con_res);

tempRange = 200:1100;
tau_res = zeros(1,length(tempRange));

for i = 1:length(tempRange)
    tau_res(i) = tau(tempRange(i));
end

tau_res = 1./tau_res;

figure;
